import React, { useEffect, useState } from "react";
import Loader from "../Components/Loader";
import Asioss from "../config/Asiosss";
import Master from "../Layout/Master";

export default function Home() {
  const [isLoading, setLoading] = useState(true);
  const [mydata, setData] = useState({ data: [] });
  const [cat, setCat] = useState([]);
  const [selectCat, setSelectCat] = useState();
  useEffect(() => {
    Asioss.get("/product").then((res) => {
      setLoading(false);
      setData(res.data.data);
    });
    Asioss.get("/category").then((res) => {
      setLoading(false);
      setCat(res.data.data);
    });
  }, []);
  console.log(cat);
  return (
    <Master>
      {isLoading ? (
        <Loader />
      ) : (
        <div className="container">
          <div className="row">
              <div>
                  <button className=""></button>
              </div>
            <div className="col-md-12">
              <div className="card p-3">
                <div>
                  {cat
                    ? cat.map((c) => (
                        <span
                          className={
                            selectCat === c.id
                              ? "btn btn-dark"
                              : "btn btn-outline-dark"
                          }
                          style={{ marginRight: "20px" }}
                          key={c.id}
                          onClick={() => {
                            setSelectCat(c.id);
                          }}
                        >
                          {c.name}
                        </span>
                      ))
                    : null}
                </div>
              </div>
            </div>
            {mydata.data
              ? mydata.data.map((d) => {
                  return (
                    <div className="col-md-3 mt-2" key={d.id}>
                      <div className="card">
                        <img
                          src={`http://127.0.0.1:8000/images/${d.image}`}
                          className="card-image"
                          alt=""
                        />
                        <div className="card-body">
                          <h4 className="text-center">{d.name}</h4>
                          <div className="d-flex justify-content-between  mt-3">
                            <span className="btn btn-sm btn-outline-warning">
                              {d.price}
                            </span>
                            <span className="btn btn-sm btn-danger">
                              Add To Cart
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })
              : null}
          </div>
        </div>
      )}
    </Master>
  );
}
