import React, { useEffect, useState } from "react";
import Loader from "../Components/Loader";
import Asioss from "../config/Asiosss";
import Master from "../Layout/Master";

export default function Home() {
  const [isLoading, setLoading] = useState(true);
  const [mydata, setData] = useState({ data: [] });
  const [cat,setCat]=useState
  useEffect(() => {
    Asioss.get("/product").then((res) => {
      setLoading(false);
      setData(res.data.data);
    });
  }, []);

  return (
    <Master>
      {isLoading ? (
        <Loader />
      ) : (
        <div className="container">
          <div className="row">
            <div className="col-md-12">
              <div className="card p-3">
                <div>
                  <span className="btn btn-outline-dark">Category Name</span>
                </div>
              </div>
            </div>
            {mydata.data
              ? mydata.data.map((d) => {
                  return (
                    <div className="col-md-3 mt-2" key={d.id}>
                      <div className="card">
                        <img
                          src={`http://127.0.0.1:8000/images/${d.image}`}
                          className="card-image"
                          alt=""
                        />
                        <div className="card-body">
                          <h4 className="text-center">{d.name}</h4>
                          <div className="d-flex justify-content-between  mt-3">
                            <span className="btn btn-sm btn-outline-warning">
                              {d.price}
                            </span>
                            <span className="btn btn-sm btn-danger">
                              Add To Cart
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })
              : null}
          </div>
        </div>
      )}
    </Master>
  );
}
